-- ----------------------------
-- Table structure for bisaijishutongji
-- ----------------------------
DROP TABLE IF EXISTS `bisaijishutongji`;
CREATE TABLE `bisaijishutongji`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `saishimingcheng` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '赛事名称',
  `bisaishijian` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '比赛时间',
  `bisaichangdi` varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '比赛场地',
  `qiuduimingcheng` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '球队名称',
  `defen` int(11) NULL DEFAULT NULL COMMENT '得分',
  `fangui` int(11) NULL DEFAULT NULL COMMENT '犯规',
  `zanting` int(11) NULL DEFAULT NULL COMMENT '暂停',
  `jilushijian` datetime NULL DEFAULT NULL COMMENT '记录时间',
  `caipanzhanghao` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '裁判账号',
  `caipanxingming` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL COMMENT '裁判姓名',
  `crossuserid` bigint(20) NULL DEFAULT NULL COMMENT '跨表用户id',
  `crossrefid` bigint(20) NULL DEFAULT NULL COMMENT '跨表主键id',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 65 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci COMMENT = '比赛技术统计' ROW_FORMAT = DYNAMIC;

