INSERT INTO `discusslanqiusaishi` VALUES (1, '2026-01-29 14:10:00', 1, 4, 'file/cansaixueshengTouxiang4.jpg', '2022010010004', '21:19，烈焰冲锋队第一场就下了马威，极速闪电团后半场明显体力不足，失球太多😱', NULL, 6, 0, 0, '1,7,19,10,13,16', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (2, '2026-01-29 14:20:00', 1, 19, 'file/cansaixueshengTouxiang19.jpg', '2022010010019', '张浩然这场发挥超好！那几个快攻直接把极速闪电团逼到心理崩盘了😄', '极速闪电团别急，后面还有追回来的机会！💪', 4, 0, 0, '1,7,10,46', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (3, '2026-01-29 14:15:00', 7, 22, 'file/cansaixueshengTouxiang22.jpg', '2022010010022', '超能陆战队20:18，慕容紫英那个中距离投篮真的太稳了！快乐星球今天发挥有点不在线😅', NULL, 5, 1, 0, '19,25,34,43,37', '4');
INSERT INTO `discusslanqiusaishi` VALUES (4, '2026-01-29 14:25:00', 7, 13, 'file/cansaixueshengTouxiang13.jpg', '2022010010013', '快乐星球加油！第一场输了没关系，后面还有反超的机会。皇甫铁牛那个篮板抢的好棒😄', '谢谢铁牛的支持！后面两场我们一定找回来！💪', 3, 0, 1, '16,22,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (5, '2026-03-15 14:35:00', 25, 46, 'file/cansaixueshengTouxiang46.jpg', '2022010010046', '我们传奇之路赢了！！！😱😱😱 21:19黑马爆冷！诸葛芸芸最后那连续三球简直不可思议，独孤静篮下抢板全场第一！', NULL, 18, 2, 1, '7,4,10,13,16,19,22,25,28,31,34,37,40,43,3,6,2,8', '11,30');
INSERT INTO `discusslanqiusaishi` VALUES (6, '2026-03-15 14:45:00', 25, 1, 'file/cansaixueshengTouxiang1.jpg', '2022010010001', '😭😭 我们输了……前半场明明压着打的，后半场怎么就这么垮了。传奇之路后半场真的太疯狂了，个人表现太强…', '张浩然加油！你们小组赛的表现已经很棒了！下赛季再来！', 8, 3, 0, '4,10,16,19,22,25,28,31', '13,40,46');
INSERT INTO `discusslanqiusaishi` VALUES (7, '2026-03-15 15:00:00', 25, 43, 'file/cansaixueshengTouxiang43.jpg', '2022010010043', '黑马爆冷看得超刺激！！😱 这才是三人篮球最酷的地方——任何时候都不能放弃！赵丽颖这场也太猛了😍', NULL, 9, 0, 1, '4,7,10,13,16,19,22,25,28,31,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (8, '2026-03-22 14:40:00', 29, 19, 'file/cansaixueshengTouxiang19.jpg', '2022010010019', '元气满满队21:18！赵子龙那个关键球简直太稳了！公孙离离的传球线路清楚得不得了😄 半决赛就这么赢了！', NULL, 11, 1, 0, '1,4,7,10,13,21,25,28,37,43,46', '16');
INSERT INTO `discusslanqiusaishi` VALUES (9, '2026-03-22 14:55:00', 29, 46, 'file/cansaixueshengTouxiang46.jpg', '2022010010046', '传奇之路我们被淘汰了😭 1/4决赛的奇迹没能再延续……不过元气满满队确实实力太过硬了，服！', '丽颖别伤心！1/4决赛那场逆转烈焰冲锋队已经是赛事传奇了！🏆', 5, 0, 0, '10,16,22,34,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (10, '2026-03-22 16:15:00', 30, 43, 'file/cansaixueshengTouxiang43.jpg', '2022010010043', '王者荣耀20:19绝杀！！那最后1分差距太心跳了😱😱 尉迟恭最后那个篮下暴扣直接锁死比赛！', NULL, 14, 1, 1, '1,4,7,10,13,16,19,22,28,31,34,37,40', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (11, '2026-03-22 16:25:00', 30, 25, 'file/cansaixueshengTouxiang25.jpg', '2022010010025', '燃烧吧少年被淘汰😭😭 19:20，只差一分啊！最后那几秒钟真的太痛了，如果东方不败那球投进去……', '杰伦加油！你们今赛季已经证明了实力了！三连胜不是开玩笑的💪', 7, 0, 0, '4,10,16,22,28,34', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (12, '2026-03-29 14:20:00', 31, 46, 'file/cansaixueshengTouxiang46.jpg', '2022010010046', '传奇之路获得季军！！🥉😊 21:19，和1/4决赛那场比分一样——传奇二连翻盘！丽颖好开心！', NULL, 10, 0, 0, '1,4,7,10,13,16,22,28,34,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (13, '2026-03-29 14:30:00', 31, 25, 'file/cansaixueshengTouxiang25.jpg', '2022010010025', '燃烧吧少年第四名……虽然不开心，但丽颖恭喜你！你们传奇之路确实是这赛季的黑马💪 下次见！', '谢谢杰伦！你们三连胜的实力我都看到了，下赛季见！😄', 4, 0, 0, '7,10,16,19,34', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (14, '2026-03-29 16:25:00', 32, 43, 'file/cansaixueshengTouxiang43.jpg', '2022010010043', '🏆🏆🏆 王者荣耀夺冠！！！21:19！！ 尉迟恭那个最后的暴扣全场沸腾了😱😱😱 我们做到了！！！CHAMPION！！！', NULL, 28, 1, 2, '1,4,7,10,13,16,19,22,25,28,31,34,37,40,46,2,3,6', '8,37');
INSERT INTO `discusslanqiusaishi` VALUES (15, '2026-03-29 16:30:00', 32, 19, 'file/cansaixueshengTouxiang19.jpg', '2022010010019', '元气满满队亚军😭 公孙离离和司马光耀都拼到极限了，但今天王者荣耀的状态确实太好了……唉，恭喜你们吧😔', '子龙别伤心！你们是赛事最强的双打组合！下赛季见！💪', 12, 2, 0, '1,4,7,10,13,16,25,28,34,37,40,46', '22,28');
INSERT INTO `discusslanqiusaishi` VALUES (16, '2026-03-29 16:40:00', 32, 25, 'file/cansaixueshengTouxiang25.jpg', '2022010010025', '恭喜王者荣耀！😊 邓超超发挥太好了，关键时刻那种稳定性真的不是一般人有的。冠军当之无愧！', NULL, 8, 0, 0, '1,4,7,10,13,19,34,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (17, '2026-03-29 16:50:00', 32, 46, 'file/cansaixueshengTouxiang46.jpg', '2022010010046', '看完决赛我觉得王者荣耀确实是全赛事最完美的队伍——攻守平衡，关键球处理太稳了。冠军！🎊', NULL, 6, 0, 0, '1,4,10,13,25,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (18, '2026-03-29 17:00:00', 32, 1, 'file/cansaixueshengTouxiang1.jpg', '2022010010001', '虽然我们烈焰冲锋队早就被淘汰了，但看完整个赛事的决赛还是被打动了😊 整个赛事回味无穷！明年见！', NULL, 5, 0, 0, '4,7,10,13,16', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (19, '2026-01-29 15:35:00', 8, 19, 'file/cansaixueshengTouxiang19.jpg', '2022010010019', '元气满满队21:17，稳胜！赵子龙这场篮下控制力真的太棒了，司马光耀远投也命中率很高😄', NULL, 7, 0, 0, '1,4,7,10,13,21,46', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (20, '2026-01-29 15:40:00', 8, 22, 'file/cansaixueshengTouxiang22.jpg', '2022010010022', '进击的巨人加油！虽然输了但独孤求败那个篮板抢得超拼😄 后面还有追上的机会！', NULL, 3, 0, 0, '10,16,25', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (21, '2026-03-04 14:10:00', 13, 25, 'file/cansaixueshengTouxiang25.jpg', '2022010010025', '燃烧吧少年21:19！第一场就赢了好开心😄 尉迟敬德那个关键三分投得太漂亮了！', NULL, 6, 0, 1, '7,10,16,19,34,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (22, '2026-03-04 14:20:00', 13, 28, 'file/cansaixueshengTouxiang28.jpg', '2022010010028', '逆风翻盘队加油！我们吴彦祖今天发挥一般，但后面两场我们会找回来的💪', NULL, 4, 0, 0, '10,16,34,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (23, '2026-03-04 15:35:00', 20, 43, 'file/cansaixueshengTouxiang43.jpg', '2022010010043', '王者荣耀21:18！D组第一场就开了好头😄 邓超超和尉迟恭配合超好，令狐平今天远投全中！', NULL, 7, 0, 0, '1,4,7,10,13,19,37', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (24, '2026-03-04 15:45:00', 20, 46, 'file/cansaixueshengTouxiang46.jpg', '2022010010046', '传奇之路第一场输了没关系😄 我们后面会拼回来的！赵丽颖的发挥能接着比赛好起来！💪', NULL, 3, 0, 0, '10,16,34', NULL);
INSERT INTO `discusslanqiusaishi` VALUES (25, '2026-03-02 14:10:00', 3, 7, 'file/cansaixueshengTouxiang7.jpg', '2022010010007', '无限火力队19:17赢了！欧阳震华这场篮下太稳了😄 烈焰冲锋队第一场赢了后面被我们扳回来了哈哈！', NULL, 5, 0, 0, '4,10,13,19,22', NULL);
