-- ----------------------------
-- Procedure structure for sp_crazily_post
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_crazily_post`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_crazily_post`(
    IN p_id BIGINT,
    IN p_userid BIGINT,
    OUT p_result INT,
    OUT p_message VARCHAR(500)
)
BEGIN
    DECLARE v_post_count INT;
    DECLARE v_current_crazily INT;
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET p_result = -1;
        SET p_message = '数据库错误，踩操作失败';
    END;
    
    START TRANSACTION;
    
    -- 验证帖子是否存在
    SELECT COUNT(*) INTO v_post_count FROM forum WHERE id = p_id AND is_del = 0;
    
    IF v_post_count = 0 THEN
        SET p_result = -2;
        SET p_message = '帖子不存在或已删除';
        ROLLBACK;
    ELSE
        -- 获取当前踩数
        SELECT crazily_number INTO v_current_crazily FROM forum WHERE id = p_id;
        
        -- 更新踩数
        UPDATE forum SET crazily_number = crazily_number + 1 WHERE id = p_id;
        
        SET p_result = 1;
        SET p_message = CONCAT('踩成功，当前踩数：', v_current_crazily + 1);
        COMMIT;
    END IF;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_create_post
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_create_post`;
delimiter ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_create_post`(
    IN p_title VARCHAR(200),
    IN p_content LONGTEXT,
    IN p_userid BIGINT,
    IN p_username VARCHAR(200),
    IN p_avatarurl LONGTEXT,
    IN p_type_name VARCHAR(200),
    IN p_cover LONGTEXT,
    IN p_is_anonymous INT,
    OUT p_result INT,
    OUT p_message VARCHAR(500)
)
BEGIN
    DECLARE v_type_count INT;
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET p_result = -1;
        SET p_message = '数据库错误，帖子创建失败';
    END;
    
    START TRANSACTION;
    
    -- 验证必填字段
    IF p_title IS NULL OR p_title = '' THEN
        SET p_result = -2;
        SET p_message = '标题不能为空';
        ROLLBACK;
    ELSEIF p_content IS NULL OR p_content = '' THEN
        SET p_result = -3;
        SET p_message = '内容不能为空';
        ROLLBACK;
    ELSEIF p_userid IS NULL OR p_userid = 0 THEN
        SET p_result = -4;
        SET p_message = '用户ID不能为空';
        ROLLBACK;
    ELSEIF p_type_name IS NULL OR p_type_name = '' THEN
        SET p_result = -5;
        SET p_message = '分类不能为空';
        ROLLBACK;
    ELSE
        -- 验证分类是否存在
        SELECT COUNT(*) INTO v_type_count FROM forum_type WHERE type_name = p_type_name;
        
        IF v_type_count = 0 THEN
            SET p_result = -6;
            SET p_message = '分类不存在';
            ROLLBACK;
        ELSE
            -- 插入帖子
            INSERT INTO forum (
                addtime,
                title,
                content,
                parentid,
                userid,
                username,
                avatarurl,
                isdone,
                is_top,
                top_time,
                type_name,
                cover,
                is_anonymous,
                is_del,
                thumbsup_number,
                crazily_number
            ) VALUES (
                CURRENT_TIMESTAMP,
                p_title,
                p_content,
                0,
                p_userid,
                p_username,
                p_avatarurl,
                '开放',
                0,
                NULL,
                p_type_name,
                p_cover,
                IFNULL(p_is_anonymous, 0),
                0,
                0,
                0
            );
            
            SET p_result = 1;
            SET p_message = CONCAT('帖子创建成功，ID：', LAST_INSERT_ID());
            COMMIT;
        END IF;
    END IF;
END
;;
delimiter ;

